-- Mencegah server mendeteksi aktivitas yang mencurigakan

local Players = game:GetService("Players")
local ReplicatedStorage = game:GetService("ReplicatedStorage")
local RunService = game:GetService("RunService")
local LocalPlayer = Players.LocalPlayer

print("[Anti-Detection] Loading stealth systems...")

-- 1. Block Detection Remotes
local function blockDetectionSystems()
    local blockedPatterns = {
            "warning", "violation", "detect", "anticheat", "anti", "cheat",
                    "bot", "exploit", "hack", "script", "suspicious", "ban",
                            "integrity", "check", "verify", "validate", "monitor"
                                }
                                    
                                        local oldNamecall = getrawmetatable(game).__namecall
                                            setreadonly(getrawmetatable(game), false)
                                                
                                                    getrawmetatable(game).__namecall = function(self, ...)
                                                            local method = getnamecallmethod()
                                                                    
                                                                            if method == "FireServer" or method == "InvokeServer" then
                                                                                        local remoteName = tostring(self):lower()
                                                                                                    
                                                                                                                -- Block detection-related remotes
                                                                                                                            for _, pattern in pairs(blockedPatterns) do
                                                                                                                                            if remoteName:find(pattern) then
                                                                                                                                                                print("[BLOCKED] Detection remote: " .. tostring(self))
                                                                                                                                                                                    return -- Block the remote
                                                                                                                                                                                                    end
                                                                                                                                                                                                                end
                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                
                                                                                                                                                                                                                                        return oldNamecall(self, ...)
                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                    print("[Anti-Detection] Remote blocking active")
                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                    -- 2. Spoof Normal Player Behavior
                                                                                                                                                                                                                                                    local function spoofNormalBehavior()
                                                                                                                                                                                                                                                        -- Random delays to simulate human behavior
                                                                                                                                                                                                                                                            local function randomDelay()
                                                                                                                                                                                                                                                                    return math.random(50, 200) / 1000 -- 0.05-0.2 seconds
                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                -- Override mouse/key inputs to add natural variation
                                                                                                                                                                                                                                                                                    local oldSendKeyEvent = game:GetService("VirtualInputManager").SendKeyEvent
                                                                                                                                                                                                                                                                                        game:GetService("VirtualInputManager").SendKeyEvent = function(self, ...)
                                                                                                                                                                                                                                                                                                task.wait(randomDelay()) -- Add human-like delay
                                                                                                                                                                                                                                                                                                        return oldSendKeyEvent(self, ...)
                                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                    print("[Anti-Detection] Behavior spoofing active")
                                                                                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                                                                                    -- 3. Block Client-Side Detection Scripts
                                                                                                                                                                                                                                                                                                                    local function blockDetectionScripts()
                                                                                                                                                                                                                                                                                                                        -- Monitor for detection scripts and disable them
                                                                                                                                                                                                                                                                                                                            local function checkScript(script)
                                                                                                                                                                                                                                                                                                                                    if script:IsA("LocalScript") or script:IsA("Script") then
                                                                                                                                                                                                                                                                                                                                                local source = script.Source or ""
                                                                                                                                                                                                                                                                                                                                                            local name = script.Name:lower()
                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                    -- Check for anti-cheat keywords
                                                                                                                                                                                                                                                                                                                                                                                                local suspiciousKeywords = {"anticheat", "detection", "monitor", "check", "validate"}
                                                                                                                                                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                                                                                                                                                        for _, keyword in pairs(suspiciousKeywords) do
                                                                                                                                                                                                                                                                                                                                                                                                                                        if name:find(keyword) or source:find(keyword) then
                                                                                                                                                                                                                                                                                                                                                                                                                                                            script.Disabled = true
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("[BLOCKED] Detection script: " .. script.Name)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    break
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    -- Check existing scripts
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        for _, obj in pairs(game:GetDescendants()) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                checkScript(obj)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Monitor new scripts
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                game.DescendantAdded:Connect(checkScript)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        print("[Anti-Detection] Script monitoring active")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- 4. Hide Script Presence
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        local function hideScriptPresence()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Modify environment to hide script indicators
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                local env = getgenv()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- Clear common script detection flags
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            env.KRNL_LOADED = nil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                env.SYNAPSE_LOADED = nil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    env.SCRIPT_LOADED = nil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        env._G.ScriptLoaded = nil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                -- Override common detection functions
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local oldType = type
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        type = function(obj)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                -- Hide userdata types that might indicate exploits
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        local result = oldType(obj)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if result == "userdata" and tostring(obj):find("Script") then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            return "table" -- Spoof as normal table
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            return result
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        print("[Anti-Detection] Presence hiding active")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- 5. Network Pattern Obfuscation
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        local function obfuscateNetworkPatterns()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Add random delays between network operations
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                local lastNetworkTime = 0
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local networkDelay = 0.1
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            local oldConnect = game.ChildAdded.Connect
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                game.ChildAdded.Connect = function(self, func)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        return oldConnect(self, function(...)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local currentTime = tick()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if currentTime - lastNetworkTime < networkDelay then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                task.wait(networkDelay - (currentTime - lastNetworkTime))
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        lastNetworkTime = tick()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    return func(...)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        print("[Anti-Detection] Network obfuscation active")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- 6. Memory Protection
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        local function protectMemory()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Clear traces of exploit usage
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                local function clearTraces()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        pcall(function()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    -- Clear console logs that might contain exploit traces
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                game:GetService("LogService"):ClearOutput()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- Clear environment variables
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                for key, _ in pairs(getgenv()) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if key:lower():find("exploit") or key:lower():find("script") then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            getgenv()[key] = nil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Clear traces periodically
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                spawn(function()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        while true do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    task.wait(30) -- Every 30 seconds
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                clearTraces()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    print("[Anti-Detection] Memory protection active")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    -- Initialize all anti-detection systems
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local function initializeAntiDetection()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        pcall(blockDetectionSystems)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            pcall(spoofNormalBehavior)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                pcall(blockDetectionScripts)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    pcall(hideScriptPresence)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        pcall(obfuscateNetworkPatterns)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            pcall(protectMemory)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Main execution
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            initializeAntiDetection()

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print("[Anti-Detection] ✅ ALL SYSTEMS ACTIVE")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print("[Anti-Detection] 🛡️ Protection Level: MAXIMUM")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print("[Anti-Detection] 🔒 Detection probability: MINIMIZED")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print("[Anti-Detection] ⚠️ Use responsibly and avoid obvious patterns")

